		break;
}
/*--------------------*/

/**Robots**/
$tmp = $modx->resource->get(\'searchable\');
if ($tmp == 1) {$output["metax.robots"] = "index, follow";}
else {$output["metax.robots"] = "noindex, nofollow";}
