if ($pragma == 1)
{
	if($html5 != 1) {$output["metax.cache"] = "cache";}
	else {$output["metax.cache"] = "public";}
}
else {$output["metax.cache"] = "no-cache";}

/**Canonical Link**/
if (!isset($urlExtension))
{
	$urlExtension = \'\';
